  `id` INT(10) NOT NULL AUTO_INCREMENT,
  `pathway_id` INT(10) NOT NULL DEFAULT '0',
  `block_name` VARCHAR(250) NOT NULL,
  `block_type` ENUM('M','A') NOT NULL DEFAULT 'M',
